import React from 'react';
import './Modal.css'; // モーダル用のCSSを作成するためのファイルをインポート

const Modal = ({ message, onClose }) => {
  return (
    <div className="modal-overlay">
      <div className="modal">
        <h2>{message}</h2>
        <button onClick={onClose}>閉じる</button>
      </div>
    </div>
  );
};

export default Modal;
