import React, { useState } from 'react';
import './App.css';

function BookClimbing() {
  const [books, setBooks] = useState([]); // ÁôªÈå≤„Åï„Çå„ÅüÊú¨„Çí‰øùÊåÅ„Åô„Çã
  const [title, setTitle] = useState(''); // Êú¨„ÅÆ„Çø„Ç§„Éà„É´ÂÖ•ÂäõÂÄ§
  const [pages, setPages] = useState(''); // „Éö„Éº„Ç∏Êï∞ÂÖ•ÂäõÂÄ§
  const [error, setError] = useState(''); // „Ç®„É©„Éº„É°„ÉÉ„Çª„Éº„Ç∏

  // Á∑è„Éö„Éº„Ç∏Êï∞„ÅÆË®àÁÆó
  const totalPages = books.reduce((sum, book) => sum + parseInt(book.pages, 10), 0);

  // ÁôªÂ±±ÈÄ≤Êçó„ÅÆË®àÁÆóÔºà1„Éö„Éº„Ç∏1„Çª„É≥„ÉÅ„Å®„Åó„Å¶ÂØåÂ£´Â±±„ÇíÂü∫Ê∫ñÔºâ
  const progress = Math.min((totalPages / 377600) * 100, 100); // ÂØåÂ£´Â±±„ÅÆÈ´ò„Åï„ÅØ377,600„Çª„É≥„ÉÅ„É°„Éº„Éà„É´

  // Êú¨„ÅÆËøΩÂä†Âá¶ÁêÜ
  const addBook = () => {
    if (!title || !pages || isNaN(pages)) {
      setError('„Çø„Ç§„Éà„É´„Å®ÊúâÂäπ„Å™„Éö„Éº„Ç∏Êï∞„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
      return;
    }

    // Êñ∞„Åó„ÅÑÊú¨„Çí„É™„Çπ„Éà„Å´ËøΩÂä†
    setBooks([...books, { title, pages: parseInt(pages, 10) }]);

    // ÂÖ•Âäõ„Éï„Ç£„Éº„É´„Éâ„Çí„ÇØ„É™„Ç¢
    setTitle('');
    setPages('');
    setError(''); // „Ç®„É©„Éº„É°„ÉÉ„Çª„Éº„Ç∏„Çí„ÇØ„É™„Ç¢
  };

  // Êú¨„ÇíÂâäÈô§„Åô„ÇãÂá¶ÁêÜ
  const removeBook = (index) => {
    const updatedBooks = books.filter((_, i) => i !== index); // ÊåáÂÆö„Åï„Çå„ÅüÊú¨„Çí„É™„Çπ„Éà„Åã„ÇâÂâäÈô§
    setBooks(updatedBooks);
  };

  return (
    <div className="App">
      <h1>BookClimbing</h1>
      <div className="book-form">
        <input
          type="text"
          placeholder="Êú¨„ÅÆ„Çø„Ç§„Éà„É´"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="number"
          placeholder="„Éö„Éº„Ç∏Êï∞"
          value={pages}
          onChange={(e) => setPages(e.target.value)}
        />
        <button onClick={addBook}>Êú¨„ÇíËøΩÂä†</button>
        {error && <p className="error">{error}</p>} {/* „Ç®„É©„Éº„É°„ÉÉ„Çª„Éº„Ç∏Ë°®Á§∫ */}
      </div>

      <h2>ÁôªÂ±±„ÅÆÈÄ≤Êçó: {progress.toFixed(2)}%</h2>

      <div className="mountain-container">
        {/* ËÉåÊôØÁîªÂÉè„Å´Â±±„Çí‰ΩøÁî®„Åó„ÄÅÁôªÂ±±ËÄÖ„ÅåÈÄ≤„ÇÄ */}
        <div className="mountain">
          <div
            className="climber"
            style={{ bottom: `${progress}%` }} // ÈÄ≤Êçó„Å´Âøú„Åò„Å¶ÁôªÂ±±ËÄÖ„ÅÆ‰ΩçÁΩÆ„ÇíÂ§âÊõ¥
          >
            üßò {/* ÁôªÂ±±ËÄÖ„ÅÆ„Ç¢„Ç§„Ç≥„É≥„Çíüßò„Å´Â§âÊõ¥ */}
          </div>
        </div>
      </div>

      <h3>Ë™≠„Çì„Å†Êú¨„ÅÆ„É™„Çπ„Éà</h3>
      {books.length > 0 ? (
        <ul>
          {books.map((book, index) => (
            <li key={index}>
              {book.title} - {book.pages}„Éö„Éº„Ç∏
              <button onClick={() => removeBook(index)} className="remove-button">ÂâäÈô§</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>„Åæ„Å†Êú¨„ÅåÁôªÈå≤„Åï„Çå„Å¶„ÅÑ„Åæ„Åõ„Çì„ÄÇ</p> /* Êú¨„Åå„Å™„ÅÑÂ†¥Âêà„ÅÆ„É°„ÉÉ„Çª„Éº„Ç∏ */
      )}
    </div>
  );
}

export default BookClimbing;
