// src/App.js
import React, { useState } from 'react';
import './App.css';

function BookClimbing() {
  // 本のタイトルとページ数を管理するための状態を定義
  const [books, setBooks] = useState([]);
  const [title, setTitle] = useState('');
  const [pages, setPages] = useState('');

  // 登録済みの総ページ数を計算
  const totalPages = books.reduce((sum, book) => sum + parseInt(book.pages), 0);

  // 登山の進捗を1ページ1センチメートルとしたときの進捗
  const progress = (totalPages / 377600) * 100; // 富士山の高さは377,600センチメートル

  // 本を追加する処理
  const addBook = () => {
    if (title && pages) {
      setBooks([...books, { title, pages }]);
      setTitle('');
      setPages('');
    }
  };

  return (
    <div className="App">
      <h1>BookClimbing</h1>
      <div className="book-form">
        <input
          type="text"
          placeholder="本のタイトル"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="number"
          placeholder="ページ数"
          value={pages}
          onChange={(e) => setPages(e.target.value)}
        />
        <button onClick={addBook}>本を追加</button>
      </div>

      <h2>登山の進捗: {progress.toFixed(2)}%</h2>
      <div className="progress-bar">
        <div className="progress" style={{ width: `${progress}%` }}></div>
      </div>

      <h3>読んだ本のリスト</h3>
      <ul>
        {books.map((book, index) => (
          <li key={index}>
            {book.title} - {book.pages}ページ
          </li>
        ))}
      </ul>
    </div>
  );
}

export default BookClimbing;
