import React, { useState, useEffect } from 'react';
import './App.css';

const App = () => {
  const [title, setTitle] = useState('');
  const [books, setBooks] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const [error, setError] = useState('');

  // 本のリストを取得
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await fetch('http://localhost:5000/books');
        const data = await response.json();
        setBooks(data);
      } catch (error) {
        setError('本の取得に失敗しました。');
      }
    };

    fetchBooks();
  }, []);

  // 本を追加する関数
  const addBook = async (selectedBook) => {
    if (!selectedBook || isNaN(selectedBook.pages)) {
      setError('有効な本を選択してください');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/books', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(selectedBook),
      });

      const newBook = await response.json();
      setBooks([...books, newBook]);

      // 入力フィールドをクリア
      setTitle('');
      setError('');
      setSearchResults([]);
    } catch (error) {
      setError('本の登録に失敗しました。');
    }
  };

  // 本を削除する関数
  const removeBook = async (id) => {
    try {
      // バックエンドにDELETEリクエストを送信
      await fetch(`http://localhost:5000/books/${id}`, {
        method: 'DELETE',
      });

      // 削除した本をフロントエンド側のリストからも除外
      setBooks(books.filter(book => book._id !== id));
    } catch (error) {
      setError('本の削除に失敗しました。');
    }
  };

  return (
    <div className="App">
      <h1>読んだ本のリスト</h1>
      {error && <p className="error">{error}</p>}
      <ul>
        {books.map((book) => (
          <li key={book._id}>
            {book.title} ({book.pages}ページ)
            <button className="remove-button" onClick={() => removeBook(book._id)}>×</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
