import React, { useState } from 'react';
import './App.css';

function BookClimbing() {
  const [books, setBooks] = useState([]); // ÁôªÈå≤„Åï„Çå„ÅüÊú¨„Çí‰øùÊåÅ„Åô„Çã
  const [title, setTitle] = useState(''); // Êú¨„ÅÆ„Çø„Ç§„Éà„É´ÂÖ•ÂäõÂÄ§
  const [error, setError] = useState(''); // „Ç®„É©„Éº„É°„ÉÉ„Çª„Éº„Ç∏
  const [searchResults, setSearchResults] = useState([]); // Ê§úÁ¥¢ÁµêÊûú

  // Google Books API„ÅßÊú¨„ÇíÊ§úÁ¥¢
  const searchBooks = async (query) => {
    if (!query) return;
    const apiKey = 'AIzaSyD4W7VvifRsmu0PkDUxwDP-4U4QfPnBQAQ'; // Google Books API„ÅÆAPI„Ç≠„Éº
    const response = await fetch(
      `https://www.googleapis.com/books/v1/volumes?q=${query}&key=${apiKey}`
    );
    const data = await response.json();
    if (data.items) {
      const books = data.items.map((item) => ({
        title: item.volumeInfo.title,
        pages: item.volumeInfo.pageCount || '‰∏çÊòé',
        imageUrl: item.volumeInfo.imageLinks ? item.volumeInfo.imageLinks.thumbnail : null // Êú¨„ÅÆÁîªÂÉèURL„ÇíÂèñÂæó
      }));
      setSearchResults(books);
    } else {
      setSearchResults([]);
    }
  };

  // „Çø„Ç§„Éà„É´„ÅåÂÖ•Âäõ„Åï„Çå„ÅüÊôÇ„Å´Êú¨„ÇíÊ§úÁ¥¢„Åô„Çã
  const handleTitleChange = (e) => {
    const newTitle = e.target.value;
    setTitle(newTitle);
    searchBooks(newTitle); // Ê§úÁ¥¢ÁµêÊûú„ÇíÊõ¥Êñ∞
  };

  // Êú¨„ÇíËøΩÂä†„Åô„ÇãÂá¶ÁêÜ
  const addBook = async (selectedBook) => {
    if (!selectedBook || isNaN(selectedBook.pages)) {
      setError('ÊúâÂäπ„Å™Êú¨„ÇíÈÅ∏Êäû„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
      return;
    }

    try {
      // „Éê„ÉÉ„ÇØ„Ç®„É≥„Éâ„Å´Êú¨„ÅÆ„Éá„Éº„Çø„ÇíÈÄÅ‰ø°„Åó„Å¶ÁôªÈå≤
      const response = await fetch('http://localhost:5000/books', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(selectedBook),
      });

      const newBook = await response.json();
      setBooks([...books, newBook]); // „Éï„É≠„É≥„Éà„Ç®„É≥„ÉâÂÅ¥„ÅÆ„É™„Çπ„Éà„Å´ËøΩÂä†

      // ÂÖ•Âäõ„Éï„Ç£„Éº„É´„Éâ„Çí„ÇØ„É™„Ç¢
      setTitle('');
      setError('');
      setSearchResults([]);
    } catch (error) {
      setError('Êú¨„ÅÆÁôªÈå≤„Å´Â§±Êïó„Åó„Åæ„Åó„Åü„ÄÇ');
    }
  };

  // Êú¨„ÇíÂâäÈô§„Åô„ÇãÂá¶ÁêÜ
  const removeBook = (index) => {
    const updatedBooks = books.filter((_, i) => i !== index); // ÊåáÂÆö„Åï„Çå„ÅüÊú¨„Çí„É™„Çπ„Éà„Åã„ÇâÂâäÈô§
    setBooks(updatedBooks);
  };

  // „Éá„Éº„Çø„Éô„Éº„Çπ„Åã„ÇâÊú¨„ÅÆ„É™„Çπ„Éà„ÇíÂèñÂæó„Åô„ÇãÂá¶ÁêÜ
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await fetch('http://localhost:5000/books');
        const data = await response.json();
        setBooks(data); // „Éï„É≠„É≥„Éà„Ç®„É≥„Éâ„ÅÆÊú¨„É™„Çπ„Éà„ÇíÊõ¥Êñ∞
      } catch (error) {
        setError('Êú¨„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü„ÄÇ');
      }
    };

    fetchBooks(); // ÂàùÂõû„É¨„É≥„ÉÄ„É™„É≥„Ç∞ÊôÇ„Å´ÂÆüË°å
  }, []);

  // Á∑è„Éö„Éº„Ç∏Êï∞„ÅÆË®àÁÆó
  const totalPages = books.reduce((sum, book) => sum + parseInt(book.pages, 10), 0);

  // ÁôªÂ±±ÈÄ≤Êçó„ÅÆË®àÁÆóÔºà1„Éö„Éº„Ç∏1„Çª„É≥„ÉÅ„Å®„Åó„Å¶ÂØåÂ£´Â±±„ÇíÂü∫Ê∫ñÔºâ
  const progress = Math.min((totalPages / 377600) * 100, 100); // ÂØåÂ£´Â±±„ÅÆÈ´ò„Åï„ÅØ377,600„Çª„É≥„ÉÅ„É°„Éº„Éà„É´

  return (
    <div className="App">
      <h1>BookClimbing</h1>
      <div className="book-form">
        <input
          type="text"
          placeholder="Êú¨„ÅÆ„Çø„Ç§„Éà„É´„ÇíÂÖ•Âäõ„Åó„Å¶Ê§úÁ¥¢"
          value={title}
          onChange={handleTitleChange}
        />
        {searchResults.length > 0 && (
          <ul className="search-results">
            {searchResults.map((book, index) => (
              <li key={index} onClick={() => addBook(book)}>
                {book.imageUrl && <img src={book.imageUrl} alt={book.title} />}
                {book.title} - {book.pages}„Éö„Éº„Ç∏
              </li>
            ))}
          </ul>
        )}
        {error && <p className="error">{error}</p>}
      </div>

      <h2>ÁôªÂ±±„ÅÆÈÄ≤Êçó: {progress.toFixed(2)}%</h2>

      <div className="mountain-container">
        <div className="mountain">
          <div
            className="climber"
            style={{ bottom: `${progress}%` }}
          >
            üßò {/* ÁôªÂ±±ËÄÖ„ÅÆ„Ç¢„Ç§„Ç≥„É≥„Çíüßò„Å´Â§âÊõ¥ */}
          </div>
        </div>
      </div>

      <h3>Ë™≠„Çì„Å†Êú¨„ÅÆ„É™„Çπ„Éà</h3>
      {books.length > 0 ? (
        <div className="book-list">
          {books.map((book, index) => (
            <div key={index} className="book-card">
              {book.imageUrl && (
                <>
                  <img src={book.imageUrl} alt={book.title} className="book-card-image" />
                  <button onClick={() => removeBook(index)} className="remove-button">√ó</button>
                </>
              )}
            </div>
          ))}
        </div>
      ) : (
        <p>„Åæ„Å†Êú¨„ÅåÁôªÈå≤„Åï„Çå„Å¶„ÅÑ„Åæ„Åõ„Çì„ÄÇ</p>
      )}
    </div>
  );
}

export default BookClimbing;
