import React, { useState } from 'react';
import './App.css';
import Modal from './Modal'; // モーダルコンポーネントをインポート

function BookClimbing() {
  const [books, setBooks] = useState([]); // 登録された本を保持する
  const [title, setTitle] = useState(''); // 本のタイトル入力値
  const [pages, setPages] = useState(''); // ページ数入力値
  const [error, setError] = useState(''); // エラーメッセージ
  const [showModal, setShowModal] = useState(false); // モーダル表示制御

  // 総ページ数の計算
  const totalPages = books.reduce((sum, book) => sum + parseInt(book.pages, 10), 0);

  // 登山進捗の計算（1ページ1センチとして富士山を基準）
  const progress = Math.min((totalPages / 377600) * 100, 100); // 富士山の高さは377,600センチメートル

  // 本の追加処理
  const addBook = () => {
    if (!title || !pages || isNaN(pages)) {
      setError('タイトルと有効なページ数を入力してください');
      return;
    }

    // 新しい本をリストに追加
    setBooks([...books, { title, pages: parseInt(pages, 10) }]);

    // 入力フィールドをクリア
    setTitle('');
    setPages('');
    setError(''); // エラーメッセージをクリア
  };

  // 本を削除する処理
  const removeBook = (index) => {
    const updatedBooks = books.filter((_, i) => i !== index); // 指定された本をリストから削除
    setBooks(updatedBooks);
  };

  // 進捗率が100%を超えたときの処理
  if (progress > 100 && !showModal) {
    setShowModal(true);
  }

  // モーダルを閉じる処理
  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <div className="App">
      <h1>BookClimbing</h1>
      <div className="book-form">
        <input
          type="text"
          placeholder="本のタイトル"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="number"
          placeholder="ページ数"
          value={pages}
          onChange={(e) => setPages(e.target.value)}
        />
        <button onClick={addBook}>本を追加</button>
        {error && <p className="error">{error}</p>} {/* エラーメッセージ表示 */}
      </div>

      <h2>登山の進捗: {progress.toFixed(2)}%</h2>

      <div className="mountain-container">
        {/* 背景画像に山を使用し、登山者が進む */}
        <div className="mountain">
          <div
            className="climber"
            style={{ bottom: `${progress}%` }} // 進捗に応じて登山者の位置を変更
          >
            🧘 {/* 登山者のアイコンを🧘に変更 */}
          </div>
        </div>
      </div>

      <h3>読んだ本のリスト</h3>
      {books.length > 0 ? (
        <ul>
          {books.map((book, index) => (
            <li key={index}>
              {book.title} - {book.pages}ページ
              <button onClick={() => removeBook(index)} className="remove-button">削除</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>まだ本が登録されていません。</p> //* 本がない場合のメッセージ */
      )}

      {/* モーダルが表示される場合 */}
      {showModal && <Modal message="🎉 おめでとうございます！読書の目標を達成しました！ 🎉" onClose={closeModal} />}
    </div>
  );
}

export default BookClimbing;
