import React, { useState } from 'react';

function App() {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedBooks, setSelectedBooks] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(-1); // 選択されたアイテムのインデックス

  // ダミーの本のデータ
  const books = [
    { title: 'Book 1', image: 'path/to/book1.jpg' },
    { title: 'Book 2', image: 'path/to/book2.jpg' },
    { title: 'Book 3', image: 'path/to/book3.jpg' },
  ];

  // 検索処理
  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
    if (e.target.value === '') {
      setSearchResults([]);
    } else {
      const results = books.filter(book =>
        book.title.toLowerCase().includes(e.target.value.toLowerCase())
      );
      setSearchResults(results);
    }
    setSelectedIndex(-1); // 入力が変更されるたびに選択をリセット
  };

  // キーボード操作の処理
  const handleKeyDown = (e) => {
    if (e.key === 'ArrowDown') {
      // 下キー
      setSelectedIndex(prevIndex =>
        prevIndex < searchResults.length - 1 ? prevIndex + 1 : prevIndex
      );
    } else if (e.key === 'ArrowUp') {
      // 上キー
      setSelectedIndex(prevIndex => (prevIndex > 0 ? prevIndex - 1 : 0));
    } else if (e.key === 'Enter' && selectedIndex >= 0) {
      // Enterキーで選択
      const selectedBook = searchResults[selectedIndex];
      alert(`You selected: ${selectedBook.title}`);
      setSearchTerm(selectedBook.title);
      setSelectedBooks([...selectedBooks, selectedBook]);
      setSearchResults([]);
    }
  };

  // 本のリストから削除する
  const removeBook = (indexToRemove) => {
    const updatedBooks = selectedBooks.filter((_, index) => index !== indexToRemove);
    setSelectedBooks(updatedBooks);
  };

  return (
    <div className="App">
      <h1>BookClimbing</h1>
      <div className="book-form">
        <input
          type="text"
          value={searchTerm}
          onChange={handleSearch}
          onKeyDown={handleKeyDown} // キーボード入力を監視
          placeholder="Search for a book"
        />
        {searchResults.length > 0 && (
          <ul className="search-results">
            {searchResults.map((book, index) => (
              <li
                key={index}
                className={index === selectedIndex ? 'selected' : ''}
              >
                <img src={book.image} alt={book.title} />
                {book.title}
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* 読んだ本のリスト */}
      <div className="book-list">
        {selectedBooks.map((book, index) => (
          <div className="book-card" key={index}>
            <img className="book-card-image" src={book.image} alt={book.title} />
            <button className="remove-book" onClick={() => removeBook(index)}>
              ×
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
